import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { User } from '../../models/user.model';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit {
  currentUser$: Observable<User | null>;
  isLoginOpen = false;

  constructor(private authService: AuthService) {
    this.currentUser$ = this.authService.currentUser;
  }

  ngOnInit(): void {}

  logout(): void {
    this.authService.logout();
    // In a real app, you'd show a toast notification here.
    console.log('Logged out');
  }

  openLogin(): void {
    this.isLoginOpen = true;
  }

  onLoginDialogClose(loggedIn: boolean): void {
    this.isLoginOpen = false;
    if (loggedIn) {
      // In a real app, you'd show a toast notification here.
      console.log('Logged in');
    }
  }
}
<header class="app-header">
  <nav class="header-nav">
    <h1 class="app-title">
      <a routerLink="/">Property Palace Manager</a>
    </h1>
    <div class="header-actions">
      <ng-container *ngIf="currentUser$ | async as user; else guestView">
        <span class="welcome-message">Welcome, {{ user.username }} ({{ user.role }})</span>
        <button (click)="logout()" class="btn btn-logout">
          Logout
        </button>
      </ng-container>
      <ng-template #guestView>
        <button (click)="openLogin()" class="btn btn-login">
          Login
        </button>
      </ng-template>
    </div>
  </nav>
</header>

<app-login *ngIf="isLoginOpen" (closeDialog)="onLoginDialogClose($event)"></app-login>
