import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Observable } from 'rxjs';
import { NotificationService, Notification } from '../../services/notification.service';

@Component({
  selector: 'app-notification-modal',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './notification-modal.component.html',
  styleUrls: ['./notification-modal.component.css']
})
// This component displays notifications and confirmation dialogs.
export class NotificationModalComponent {
  notification$: Observable<Notification | null>;

  constructor(private notificationService: NotificationService) {
    this.notification$ = this.notificationService.notifications$;
  }

  onConfirm(notification: Notification): void {
    if (notification.onConfirm) {
      notification.onConfirm();
    }
    this.notificationService.hide();
  }

  onCancel(): void {
    this.notificationService.hide();
  }
}
<div *ngIf="notification$ | async as notification" class="modal-backdrop">
  <div class="modal-container" [ngClass]="'modal-' + notification.type">
    <div class="modal-header">
      <h3 class="modal-title">{{ notification.type | titlecase }}</h3>
    </div>
    <div class="modal-body">
      <p>{{ notification.message }}</p>
    </div>
    <div class="modal-footer">
      <ng-container *ngIf="notification.type === 'confirm'">
        <button (click)="onCancel()" class="btn btn-secondary">Cancel</button>
        <button (click)="onConfirm(notification)" class="btn btn-primary">Confirm</button>
      </ng-container>
      <ng-container *ngIf="notification.type !== 'confirm'">
        <button (click)="onCancel()" class="btn btn-primary">OK</button>
      </ng-container>
    </div>
  </div>
</div>
