import { Component, OnInit, Input, Output, EventEmitter } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators, FormsModule, ReactiveFormsModule } from '@angular/forms';
import { Property } from '../../models/property.model';
import { PropertyService } from '../../services/property.service';

@Component({
  selector: 'app-property-form',
  standalone: true,
  imports: [CommonModule, FormsModule, ReactiveFormsModule],
  templateUrl: './property-form.component.html',
  styleUrls: ['./property-form.component.css']
})
export class PropertyFormComponent implements OnInit {
  @Input() property: Property | null = null;
  @Output() closeDialog = new EventEmitter<boolean>();

  propertyForm: FormGroup;
  isEditing = false;

  constructor(
    private fb: FormBuilder,
    private propertyService: PropertyService
  ) {
    this.propertyForm = this.fb.group({
      name: ['', Validators.required],
      type: ['Apartment', Validators.required],
      location: ['', Validators.required],
      price: [0, [Validators.required, Validators.min(1)]],
      bedrooms: [1, [Validators.required, Validators.min(0)]],
      bathrooms: [1, [Validators.required, Validators.min(0)]],
      area: [0, [Validators.required, Validators.min(1)]],
      description: ['']
    });
  }

  ngOnInit(): void {
    if (this.property) {
      this.isEditing = true;
      this.propertyForm.patchValue(this.property);
    }
  }

  onSubmit(): void {
    if (this.propertyForm.invalid) {
      // In a real app, you'd show a toast notification for validation errors.
      console.error('Form is invalid');
      return;
    }

    if (this.isEditing && this.property) {
      const updatedProperty: Property = {
        ...this.property,
        ...this.propertyForm.value
      };
      this.propertyService.updateProperty(updatedProperty);
    } else {
      this.propertyService.addProperty(this.propertyForm.value);
    }

    this.closeDialog.emit(true);
  }

  close(refresh: boolean = false): void {
    this.closeDialog.emit(refresh);
  }
}
<div class="modal-backdrop" (click)="close()"></div>
<div class="modal-container">
  <div class="modal-content">
    <div class="modal-header">
      <h3 class="modal-title">{{ isEditing ? 'Edit Property' : 'Add New Property' }}</h3>
      <button (click)="close()" class="btn-close">&times;</button>
    </div>
    <form [formGroup]="propertyForm" (ngSubmit)="onSubmit()" class="modal-body">
      <div class="form-grid">
        <div class="form-group">
          <label for="name" class="block text-sm font-medium text-gray-700">Property Name *</label>
          <input id="name" formControlName="name" type="text" class="mt-1 block w-full border-gray-300 rounded-md shadow-sm">
        </div>
        <div class="form-group">
          <label for="type" class="block text-sm font-medium text-gray-700">Property Type</label>
          <select id="type" formControlName="type" class="mt-1 block w-full border-gray-300 rounded-md shadow-sm">
            <option>Apartment</option>
            <option>House</option>
            <option>Condo</option>
            <option>Townhouse</option>
            <option>Commercial</option>
          </select>
        </div>
      </div>

      <div class="form-group">
        <label for="location" class="block text-sm font-medium text-gray-700">Location *</label>
        <input id="location" formControlName="location" type="text" class="mt-1 block w-full border-gray-300 rounded-md shadow-sm">
      </div>

      <div class="form-grid-col3">
        <div class="form-group">
          <label for="price" class="block text-sm font-medium text-gray-700">Price *</label>
          <input id="price" formControlName="price" type="number" class="mt-1 block w-full border-gray-300 rounded-md shadow-sm">
        </div>
        <div class="form-group">
          <label for="bedrooms" class="block text-sm font-medium text-gray-700">Bedrooms</label>
          <input id="bedrooms" formControlName="bedrooms" type="number" class="mt-1 block w-full border-gray-300 rounded-md shadow-sm">
        </div>
        <div class="form-group">
          <label for="bathrooms" class="block text-sm font-medium text-gray-700">Bathrooms</label>
          <input id="bathrooms" formControlName="bathrooms" type="number" class="mt-1 block w-full border-gray-300 rounded-md shadow-sm">
        </div>
      </div>

      <div class="form-group">
        <label for="area" class="block text-sm font-medium text-gray-700">Area (sqft) *</label>
        <input id="area" formControlName="area" type="number" class="mt-1 block w-full border-gray-300 rounded-md shadow-sm">
      </div>

      <div class="form-group">
        <label for="description" class="block text-sm font-medium text-gray-700">Description</label>
        <textarea id="description" formControlName="description" rows="3" class="mt-1 block w-full border-gray-300 rounded-md shadow-sm"></textarea>
      </div>

      <div class="modal-footer">
        <button type="button" (click)="close()" class="bg-gray-200 hover:bg-gray-300 text-gray-800 font-bold py-2 px-4 rounded">Cancel</button>
        <button type="submit" [disabled]="propertyForm.invalid" class="">{{ isEditing ? 'Update Property' : 'Create Property' }}</button>
      </div>
    </form>
  </div>
</div>
