import { Component, OnInit, OnDestroy } from '@angular/core';
import { Observable, Subscription, BehaviorSubject, combineLatest } from 'rxjs';
import { switchMap, startWith } from 'rxjs/operators';
import { Property } from '../../models/property.model';
import { User } from '../../models/user.model';
import { PropertyService } from '../../services/property.service';
import { AuthService } from '../../services/auth.service';

const ITEMS_PER_PAGE = 6;

@Component({
  selector: 'app-property-list',
  templateUrl: './property-list.component.html',
  styleUrls: ['./property-list.component.css']
})
export class PropertyListComponent implements OnInit, OnDestroy {
  properties$: Observable<Property[]>;
  paginatedProperties$: Observable<Property[]>;
  uniqueLocations$: Observable<string[]>;
  currentUser$: Observable<User | null>;

  searchTerm = new BehaviorSubject<string>('');
  filterType = new BehaviorSubject<string>('all');
  filterLocation = new BehaviorSubject<string>('all');
  sortBy = new BehaviorSubject<string>('name');
  sortOrder = new BehaviorSubject<'asc' | 'desc'>('asc');
  currentPage = new BehaviorSubject<number>(1);

  totalPages = 0;
  viewMode: 'grid' | 'table' = 'grid';

  isPropertyFormOpen = false;
  editingProperty: Property | null = null;

  private subscriptions = new Subscription();

  constructor(
    private propertyService: PropertyService,
    private authService: AuthService
  ) {
    this.currentUser$ = this.authService.currentUser;
    this.uniqueLocations$ = this.propertyService.getUniqueLocations();

    this.properties$ = combineLatest([
      this.searchTerm,
      this.filterType,
      this.filterLocation,
      this.sortBy,
      this.sortOrder
    ]).pipe(
      switchMap(([searchTerm, filterType, filterLocation, sortBy, sortOrder]) =>
        this.propertyService.getProperties(searchTerm, filterType, filterLocation, sortBy, sortOrder)
      )
    );

    this.paginatedProperties$ = combineLatest([
      this.properties$,
      this.currentPage.pipe(startWith(1))
    ]).pipe(
      switchMap(([properties, currentPage]) => {
        this.totalPages = Math.ceil(properties.length / ITEMS_PER_PAGE);
        const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
        return new BehaviorSubject(properties.slice(startIndex, startIndex + ITEMS_PER_PAGE));
      })
    );
  }

  ngOnInit(): void {}

  ngOnDestroy(): void {
    this.subscriptions.unsubscribe();
  }

  onSearch(event: Event): void {
    const value = (event.target as HTMLInputElement).value;
    this.searchTerm.next(value);
    this.currentPage.next(1);
  }

  onFilterTypeChange(value: string): void {
    this.filterType.next(value);
    this.currentPage.next(1);
  }

  onFilterLocationChange(value: string): void {
    this.filterLocation.next(value);
    this.currentPage.next(1);
  }

  onSortChange(value: string): void {
    this.sortBy.next(value);
  }

  toggleSortOrder(): void {
    this.sortOrder.next(this.sortOrder.value === 'asc' ? 'desc' : 'asc');
  }

  goToPage(page: number): void {
    if (page >= 1 && page <= this.totalPages) {
      this.currentPage.next(page);
    }
  }

  openPropertyForm(property: Property | null): void {
    this.editingProperty = property;
    this.isPropertyFormOpen = true;
  }

  onPropertyFormClose(refresh: boolean): void {
    this.isPropertyFormOpen = false;
    this.editingProperty = null;
    if (refresh) {
      // This will trigger a refresh of the properties list
      this.propertyService.properties$.subscribe();
    }
  }

  deleteProperty(id: string): void {
    if (confirm('Are you sure you want to delete this property?')) {
      this.propertyService.deleteProperty(id);
      // In a real app, you'd show a toast notification here.
      console.log('Property deleted');
    }
  }

  exportToCSV(): void {
    this.subscriptions.add(
      this.properties$.subscribe(properties => {
        this.propertyService.exportToCSV(properties);
        // In a real app, you'd show a toast notification here.
        console.log('Exported to CSV');
      })
    );
  }
}
<div class="container mx-auto p-4">
  <!-- Filters and Actions -->
  <div class="bg-white p-4 rounded-lg shadow-md mb-4">
    <div class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
      <!-- Search -->
      <div class="relative">
        <input
          type="text"
          placeholder="Search by name or location..."
          [value]="searchTerm.value"
          (input)="onSearch($event)"
          class="w-full p-2 border rounded-md pl-10"
        />
      </div>

      <!-- Filter by Type -->
      <select (change)="onFilterTypeChange($any($event.target).value)" class="w-full p-2 border rounded-md">
        <option value="all">All Types</option>
        <option value="Apartment">Apartment</option>
        <option value="House">House</option>
        <option value="Condo">Condo</option>
        <option value="Townhouse">Townhouse</option>
        <option value="Commercial">Commercial</option>
      </select>

      <!-- Filter by Location -->
      <select (change)="onFilterLocationChange($any($event.target).value)" class="w-full p-2 border rounded-md">
        <option value="all">All Locations</option>
        <option *ngFor="let location of uniqueLocations$ | async" [value]="location">{{ location }}</option>
      </select>

      <!-- Sort -->
      <div class="flex items-center">
        <select (change)="onSortChange($any($event.target).value)" class="w-full p-2 border rounded-md">
          <option value="name">Sort by Name</option>
          <option value="price">Sort by Price</option>
          <option value="dateAdded">Sort by Date Added</option>
        </select>
        <button (click)="toggleSortOrder()" class="p-2 border rounded-md ml-2">
          {{ sortOrder.value === 'asc' ? '↑' : '↓' }}
        </button>
      </div>
    </div>

    <!-- Action Buttons -->
    <div class="mt-4 flex justify-between items-center">
      <div>
        <button *ngIf="(currentUser$ | async)?.role === 'Admin'" (click)="openPropertyForm(null)" class="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded">
          Add Property
        </button>
        <button (click)="exportToCSV()" class="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded ml-2">
          Export to CSV
        </button>
      </div>
      <div>
        <button (click)="viewMode = 'grid'" [ngClass]="{'bg-gray-400': viewMode === 'grid'}" class="p-2 border rounded-md">
          Grid
        </button>
        <button (click)="viewMode = 'table'" [ngClass]="{'bg-gray-400': viewMode === 'table'}" class="p-2 border rounded-md ml-2">
          Table
        </button>
      </div>
    </div>
  </div>

  <!-- Property Display -->
  <ng-container *ngIf="(paginatedProperties$ | async) as properties">
    <div *ngIf="viewMode === 'grid'" class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
      <div *ngFor="let property of properties" class="bg-white rounded-lg shadow-lg overflow-hidden">
        <!-- Property Card Content -->
        <div class="p-6">
          <h3 class="text-xl font-bold mb-2">{{ property.name }}</h3>
          <p class="text-gray-600 mb-4">{{ property.location }}</p>
          <div class="flex justify-between items-center text-lg font-semibold">
            <span>{{ property.price | currency }}</span>
            <span class="text-sm text-gray-500">{{ property.area }} sqft</span>
          </div>
          <div class="mt-4 flex justify-end space-x-2" *ngIf="(currentUser$ | async)?.role === 'Admin'">
            <button (click)="openPropertyForm(property)" class="text-blue-500 hover:text-blue-700">Edit</button>
            <button (click)="deleteProperty(property.id)" class="text-red-500 hover:text-red-700">Delete</button>
          </div>
        </div>
      </div>
    </div>

    <div *ngIf="viewMode === 'table'" class="bg-white rounded-lg shadow-md overflow-x-auto">
      <table class="min-w-full divide-y divide-gray-200">
        <thead class="bg-gray-50">
          <tr>
            <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Name</th>
            <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Type</th>
            <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Location</th>
            <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Price</th>
            <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
          </tr>
        </thead>
        <tbody class="bg-white divide-y divide-gray-200">
          <tr *ngFor="let property of properties">
            <td class="px-6 py-4 whitespace-nowrap">{{ property.name }}</td>
            <td class="px-6 py-4 whitespace-nowrap">{{ property.type }}</td>
            <td class="px-6 py-4 whitespace-nowrap">{{ property.location }}</td>
            <td class="px-6 py-4 whitespace-nowrap">{{ property.price | currency }}</td>
            <td class="px-6 py-4 whitespace-nowrap text-sm font-medium" *ngIf="(currentUser$ | async)?.role === 'Admin'">
              <button (click)="openPropertyForm(property)" class="text-indigo-600 hover:text-indigo-900">Edit</button>
              <button (click)="deleteProperty(property.id)" class="text-red-600 hover:text-red-900 ml-4">Delete</button>
            </td>
          </tr>
        </tbody>
      </table>
    </div>

    <!-- Pagination -->
    <div class="mt-6 flex justify-center items-center space-x-2">
      <button (click)="goToPage((currentPage.value) - 1)" [disabled]="currentPage.value === 1" class="px-4 py-2 bg-white border rounded-md">Prev</button>
      <span>Page {{ currentPage.value }} of {{ totalPages }}</span>
      <button (click)="goToPage((currentPage.value) + 1)" [disabled]="currentPage.value === totalPages" class="px-4 py-2 bg-white border rounded-md">Next</button>
    </div>
  </ng-container>
</div>

<app-property-form 
  *ngIf="isPropertyFormOpen" 
  [property]="editingProperty" 
  (closeDialog)="onPropertyFormClose($event)">
</app-property-form>
