import { Component, EventEmitter, Output } from '@angular/core';
import { CommonModule } from '@angular/common';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  @Output() closeDialog = new EventEmitter<boolean>();

  constructor(private authService: AuthService) { }

  login(role: 'Admin' | 'User'): void {
    const username = role === 'Admin' ? 'Admin User' : 'Regular User';
    this.authService.login(username, role);
    this.closeDialog.emit(true);
    // In a real app, you'd show a toast notification here.
    console.log(`Logged in as ${role}`);
  }

  close(): void {
    this.closeDialog.emit(false);
  }
}
<div class="login-overlay" (click)="close()"></div>
<div class="login-dialog-container">
  <div class="login-dialog">
    <div class="dialog-header">
      <h3>Login to Property Palace</h3>
      <button (click)="close()" class="close-button">&times;</button>
    </div>
    <div class="dialog-body">
      <p class="dialog-subheading">
        For demonstration purposes, please select a role to log in.
      </p>
      <div class="button-group">
        <button (click)="login('Admin')" class="btn btn-primary">
          Login as Admin
        </button>
        <button (click)="login('User')" class="btn btn-secondary">
          Login as User
        </button>
      </div>
      <p class="dialog-footer-text">
        Admin: Full CRUD access | User: Read-only access
      </p>
    </div>
  </div>
</div>
