import { Component, OnInit, OnDestroy } from '@angular/core';
import { CommonModule } from '@angular/common';
import { PropertyFormComponent } from '../property-form/property-form.component';
import { Observable, Subscription, BehaviorSubject, combineLatest } from 'rxjs';
import { switchMap, startWith } from 'rxjs/operators';
import { Property } from '../../models/property.model';
import { User } from '../../models/user.model';
import { PropertyService } from '../../services/property.service';
import { AuthService } from '../../services/auth.service';
import { RouterModule } from '@angular/router';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';
import { NotificationService } from '../../services/notification.service';
const ITEMS_PER_PAGE = 6;

@Component({
  selector: 'app-property-list',
  standalone: true,
  imports: [
    CommonModule,
    RouterModule,
    FormsModule,
    ReactiveFormsModule,
    PropertyFormComponent,
  ],
  templateUrl: './property-list.component.html',
  styleUrls: ['./property-list.component.css']
})
// NOTE: Stale lint errors about unused imports may appear due to an editor cache issue.
// These can be disregarded if the application builds successfully.
// This component displays the list of properties and handles filtering, sorting, and CRUD operations.
export class PropertyListComponent implements OnInit, OnDestroy {
  properties$: Observable<Property[]>;
  paginatedProperties$: Observable<Property[]>;
  uniqueLocations$: Observable<string[]>;
  currentUser$: Observable<User | null>;

  searchTerm = new BehaviorSubject<string>('');
  filterType = new BehaviorSubject<string>('all');
  filterLocation = new BehaviorSubject<string>('all');
  sortBy = new BehaviorSubject<string>('name');
  sortOrder = new BehaviorSubject<'asc' | 'desc'>('asc');
  currentPage = new BehaviorSubject<number>(1);

  totalPages = 0;
  viewMode: 'grid' | 'table' = 'grid';

  isPropertyFormOpen = false;
  editingProperty: Property | null = null;

  private subscriptions = new Subscription();

  constructor(
    private propertyService: PropertyService,
    private authService: AuthService,
    private notificationService: NotificationService
  ) {
    this.currentUser$ = this.authService.currentUser;
    this.uniqueLocations$ = this.propertyService.getUniqueLocations();

    this.properties$ = combineLatest([
      this.searchTerm,
      this.filterType,
      this.filterLocation,
      this.sortBy,
      this.sortOrder
    ]).pipe(
      switchMap(([searchTerm, filterType, filterLocation, sortBy, sortOrder]) =>
        this.propertyService.getProperties(searchTerm, filterType, filterLocation, sortBy, sortOrder)
      )
    );

    this.paginatedProperties$ = this.properties$;
  }

  ngOnInit(): void {}

  ngOnDestroy(): void {
    this.subscriptions.unsubscribe();
  }

  onSearch(event: Event): void {
    const value = (event.target as HTMLInputElement).value;
    this.searchTerm.next(value);
    this.currentPage.next(1);
  }

  onFilterTypeChange(value: string): void {
    this.filterType.next(value);
    this.currentPage.next(1);
  }

  onFilterLocationChange(value: string): void {
    this.filterLocation.next(value);
    this.currentPage.next(1);
  }

  onSortChange(value: string): void {
    this.sortBy.next(value);
  }

  toggleSortOrder(): void {
    this.sortOrder.next(this.sortOrder.value === 'asc' ? 'desc' : 'asc');
  }

  goToPage(page: number): void {
    if (page >= 1 && page <= this.totalPages) {
      this.currentPage.next(page);
    }
  }

  openPropertyForm(property: Property | null): void {
    this.editingProperty = property;
    this.isPropertyFormOpen = true;
  }

  onPropertyFormClose(refresh: boolean): void {
    this.isPropertyFormOpen = false;
    this.editingProperty = null;
    if (refresh) {
      // This will trigger a refresh of the properties list
      this.propertyService.properties$.subscribe();
    }
  }

  deleteProperty(id: string): void {
    this.notificationService.showConfirmation(
      'Are you sure you want to delete this property?',
      () => {
        this.propertyService.deleteProperty(id);
        this.notificationService.showSuccess('Property deleted successfully.');
      }
    );
  }

  exportToCSV(): void {
    this.subscriptions.add(
      this.properties$.subscribe(properties => {
        this.propertyService.exportToCSV(properties);
        this.notificationService.showSuccess('Properties exported to CSV successfully!');
      })
    );
  }
}
<div class="property-list-container">
  <div class="filters-and-actions-bar">
    <div class="filters-grid">
      <input
        type="text"
        placeholder="Search by name or location..."
        [value]="searchTerm.value"
        (input)="onSearch($event)"
        class="form-input search-input"
      />
      <select (change)="onFilterTypeChange($any($event.target).value)" class="form-select">
        <option value="all">All Types</option>
        <option value="Apartment">Apartment</option>
        <option value="House">House</option>
        <option value="Condo">Condo</option>
        <option value="Townhouse">Townhouse</option>
        <option value="Commercial">Commercial</option>
      </select>
      <select (change)="onFilterLocationChange($any($event.target).value)" class="form-select">
        <option value="all">All Locations</option>
        <option *ngFor="let location of uniqueLocations$ | async" [value]="location">{{ location }}</option>
      </select>
      <div class="sort-control">
        <select (change)="onSortChange($any($event.target).value)" class="form-select">
          <option value="name">Sort by Name</option>
          <option value="price">Sort by Price</option>
          <option value="dateAdded">Sort by Date Added</option>
        </select>
        <button (click)="toggleSortOrder()" class="btn-sort">
          {{ sortOrder.value === 'asc' ? '↑' : '↓' }}
        </button>
      </div>
    </div>
    <div class="actions-toolbar">
      <div class="main-actions">
        <button *ngIf="(currentUser$ | async)?.role === 'Admin'" (click)="openPropertyForm(null)" class="btn btn-primary">
          Add Property
        </button>
        <button (click)="exportToCSV()" class="btn btn-secondary">
          Export to CSV
        </button>
      </div>
      <div class="view-toggle">
        <button (click)="viewMode = 'grid'" [class.active]="viewMode === 'grid'" class="btn-view-mode">
          Grid
        </button>
        <button (click)="viewMode = 'table'" [class.active]="viewMode === 'table'" class="btn-view-mode">
          Table
        </button>
      </div>
    </div>
  </div>

  <ng-container *ngIf="(paginatedProperties$ | async) as properties">
    <div *ngIf="properties.length > 0; else noProperties">
      <div *ngIf="viewMode === 'grid'" class="property-grid">
        <div *ngFor="let property of properties" class="property-card">
          <div class="card-content">
            <h3 class="card-title">{{ property.name }}</h3>
            <div class="card-meta">
              <div class="meta-item">
                <i class="bi bi-geo-alt-fill"></i>
                <span>{{ property.location }}</span>
              </div>
            </div>
            <div class="card-details">
              <div class="detail-item">
                <i class="bi bi-cash-coin"></i>
                <span>{{ property.price | currency }}</span>
              </div>
              <div class="detail-item">
                <i class="bi bi-rulers"></i>
                <span>{{ property.area }} sqft</span>
              </div>
            </div>
            <div class="card-actions" *ngIf="(currentUser$ | async)?.role === 'Admin'">
              <button (click)="openPropertyForm(property)" class="btn-edit">Edit</button>
              <button (click)="deleteProperty(property.id)" class="btn-delete">Delete</button>
            </div>
          </div>
        </div>
      </div>

      <div *ngIf="viewMode === 'table'" class="property-table-container">
        <table class="property-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Type</th>
              <th>Location</th>
              <th>Price</th>
              <th *ngIf="(currentUser$ | async)?.role === 'Admin'">Actions</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let property of properties">
              <td>{{ property.name }}</td>
              <td>{{ property.type }}</td>
              <td>{{ property.location }}</td>
              <td>{{ property.price | currency }}</td>
              <td *ngIf="(currentUser$ | async)?.role === 'Admin'">
                <div class="table-actions">
                  <button (click)="openPropertyForm(property)" class="btn-edit">Edit</button>
                  <button (click)="deleteProperty(property.id)" class="btn-delete">Delete</button>
                </div>
              </td>
            </tr>
          </tbody>
        </table>
      </div>

      
    </div>
  </ng-container>

  <ng-template #noProperties>
    <div class="no-properties-message">
      <p>No properties found. Try adjusting your filters or adding a new property.</p>
    </div>
  </ng-template>
</div>

<app-property-form 
  *ngIf="isPropertyFormOpen" 
  [property]="editingProperty" 
  (closeDialog)="onPropertyFormClose($event)">
</app-property-form>
