import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { CommonModule } from '@angular/common';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-auth',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './auth.component.html',
  styleUrls: ['./auth.component.css']
})
export class AuthComponent {
  constructor(private authService: AuthService, private router: Router) {}

  login(role: 'Admin' | 'User'): void {
    this.authService.login('Demo User', role);
    this.router.navigate(['/properties']);
  }
}
<div class="auth-container">
  <div class="auth-card">
    <h1 class="auth-title">Welcome to Maverick Properties</h1>
    <p class="auth-subtitle">Please select your role to continue</p>
    <div class="auth-actions">
      <button (click)="login('Admin')" class="btn btn-primary">Login as Admin</button>
      <button (click)="login('User')" class="btn btn-secondary">Login as User</button>
    </div>
  </div>
</div>
