import { Component, EventEmitter, Output } from '@angular/core';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  @Output() closeDialog = new EventEmitter<boolean>();

  constructor(private authService: AuthService) { }

  login(role: 'Admin' | 'User'): void {
    const username = role === 'Admin' ? 'Admin User' : 'Regular User';
    this.authService.login(username, role);
    this.closeDialog.emit(true);
    // In a real app, you'd show a toast notification here.
    console.log(`Logged in as ${role}`);
  }

  close(): void {
    this.closeDialog.emit(false);
  }
}
<div class="fixed inset-0 bg-black bg-opacity-50 z-40" (click)="close()"></div>
<div class="fixed inset-0 z-50 flex items-center justify-center">
  <div class="bg-white rounded-lg shadow-xl p-6 w-full max-w-md mx-auto">
    <div class="flex justify-between items-center border-b pb-3">
      <h3 class="text-lg font-semibold">Login to Property Management System</h3>
      <button (click)="close()" class="text-gray-400 hover:text-gray-600">&times;</button>
    </div>
    <div class="py-4">
      <p class="text-sm text-gray-500 mb-4 text-center">
        For demonstration purposes, please select a role to log in.
      </p>
      <div class="grid grid-cols-2 gap-4">
        <button (click)="login('Admin')" class="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded flex items-center justify-center gap-2">
          Login as Admin
        </button>
        <button (click)="login('User')" class="bg-gray-200 hover:bg-gray-300 text-gray-800 font-bold py-2 px-4 rounded flex items-center justify-center gap-2">
          Login as User
        </button>
      </div>
      <p class="text-xs text-gray-500 mt-4 text-center">
        Admin: Full CRUD access | User: Read-only access
      </p>
    </div>
  </div>
</div>
